// 1) ParÃ¢metros de controle
const MAX_CHARS = 40;   // mÃ¡ximo de caracteres por legenda
const segments = items[0].json.segments || [];

// 2) Transforma o texto e timestamps em legendas â€œcortadasâ€
const captions = [];

for (const seg of segments) {
  const words = seg.text.trim().split(" ");
  let line = "";
  let startTime = null;

  for (const w of words) {
    // Se ainda nÃ£o iniciou a linha, define startTime
    if (line === "") {
      startTime = seg.start + seg.text.indexOf(w) / seg.text.length * (seg.end - seg.start);
      // (este cÃ¡lculo Ã© sÃ³ aproximado; para precisÃ£o palavra-a-palavra vocÃª precisaria dos timepoints)
    }
    // Se adicionar esta palavra ultrapassa o limite de chars, fecha a linha atual
    if ((line + " " + w).trim().length > MAX_CHARS) {
      captions.push({ word: line.trim(), start_time: startTime, end_time: seg.start + (seg.text.indexOf(w) / seg.text.length) * (seg.end - seg.start) });
      line = w;
      startTime = seg.start + seg.text.indexOf(w) / seg.text.length * (seg.end - seg.start);
    } else {
      line = (line + " " + w).trim();
    }
  }
  // Fecha a Ãºltima linha do segmento
  if (line) {
    captions.push({ word: line, start_time: startTime, end_time: seg.end });
  }
}

// 3) Embaralha de volta num formato de itens para o prÃ³ximo nÃ³
return captions.map(c => ({ json: c }));

// 1) Pega o ID do node anterior (mesmo se estiver pinado)
const originalId = $('Buscar tabelas 1').all()[0].json.id;

// 2) ConstrÃ³i o array de legendas
const captionsArray = items.map(i => ({
  text: i.json.text,
  start_time: i.json.start,
  end_time: i.json.end,
}));

// 3) Serializa e converte para buffer
const jsonString = JSON.stringify(captionsArray, null, 2);
const buffer = Buffer.from(jsonString);

// 4) Retorna com ID + buffer
return [{
  json: { id: originalId },
  binary: {
    captionsFile: {
      data: buffer,
      fileName: 'captions.json',
      mimeType: 'application/json',
    }
  }
}];

// Initialize an array to hold IDs
const ids = [];

// Loop over input items
for (const item of $input.all()) {
  // Loop through each 'data' array
  for (const dataItem of item.json.data) {
    // Check if 'id' is defined
    if (dataItem.id) {
      // Push the ID into the array
      ids.push(dataItem.id);
    }
  }
}

// Return the array wrapped in an object
return [{ json: { ids } }];

const inputSegments = $input.all();

if (inputSegments.length > 0) {
  const lastSegment = inputSegments[inputSegments.length - 1].json;

  if (parseFloat(lastSegment.duration) < 2.0 && inputSegments.length > 1) {
    const secondLastSegment = inputSegments[inputSegments.length - 2].json;

    // Merge palavras
    secondLastSegment.words += ' ' + lastSegment.words;

    // Soma das duraÃ§Ãµes
    secondLastSegment.duration = (
      parseFloat(secondLastSegment.duration) + parseFloat(lastSegment.duration)
    ).toFixed(2);

    // Remove o Ãºltimo item
    inputSegments.pop();
  }
}

// Retorna os segmentos corrigidos (como array de items)
return inputSegments;

