// 1. Coleta todas as palavras da legenda
const palavrasOriginais = items.map(i => i.json.word);

// 2. Junta tudo em um Ãºnico array de palavras
const palavras = palavrasOriginais.join(' ').split(' ');

// 3. Agrupa em blocos de atÃ© 3 palavras
const grupos = [];
for (let i = 0; i < palavras.length; i += 3) {
  grupos.push(palavras.slice(i, i + 3).join(' '));
}

// 4. Define a duraÃ§Ã£o total com base no Ãºltimo item de Frases Curtas
const duracaoTotal = items[items.length - 1].json.end_time || 10;
const duracaoPorGrupo = duracaoTotal / grupos.length;

// 5. Gera os objetos com text, start e end
return grupos.map((texto, i) => {
  return {
    json: {
      text: texto,
      start: +(i * duracaoPorGrupo).toFixed(3),
      end: +((i + 1) * duracaoPorGrupo).toFixed(3)
    }
  };
});
